import { useState } from "react";
import { Search, Mail } from "lucide-react";

export default function DashboardPage() {
  const [searchTerm, setSearchTerm] = useState("");

  const stats = {
    inbox: 75,
    spam: 25,
  };

  const results = [
    {
      id: 1,
      name: "Shannon Ledford",
      email: "shannon@laveaocl.com",
      secondary: "Shannon Ladford@qonples.com",
      status: "Inbox",
      time: "25 mins ago",
    },
    {
      id: 2,
      name: "CYNDI B RICE",
      email: "shannongl@gmail.com",
      secondary: "Amanda Flitsch...",
      status: "Spam",
      time: "25 mins ago",
    },
    // ... more items
  ];

  const filteredResults = results.filter((item) =>
    (item.name + item.email + item.secondary)
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Top Bar with Search */}
      <div className="bg-blue-900 py-4 px-6 flex items-center justify-end">
        <div className="relative w-full max-w-md">
          <input
            type="text"
            placeholder="Enter keyword or email and press enter"
            className="w-full rounded-md px-4 py-2 pr-10 text-sm"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Search className="absolute right-2 top-2.5 h-5 w-5 text-gray-500" />
        </div>
      </div>

      {/* Stat Circles */}
      <div className="flex justify-center gap-10 py-8">
        <StatCircle label="Inbox" value={stats.inbox} color="green" />
        <StatCircle label="Spam" value={stats.spam} color="red" />
      </div>

      {/* Grid of Email Cards */}
      <div className="grid grid-cols-3 gap-4 px-10 pb-10">
        {filteredResults.map((item) => (
          <div
            key={item.id}
            className={`rounded-xl p-4 shadow-sm border ${
              item.status === "Inbox" || item.status === "inbox"
                ? "bg-green-100 border-green-300"
                : "bg-red-100 border-red-300"
            }`}
          >
            <h2 className="text-md font-semibold">{item.name}</h2>
            <p className="text-sm text-gray-700">{item.email}</p>
            <p className="text-xs text-gray-500">{item.secondary}</p>
            <div className="mt-3 flex justify-between items-center">
              <span
                className={`px-2 py-1 text-xs font-semibold rounded ${
                  item.status === "Inbox" || item.status === "inbox"
                    ? "bg-green-600 text-white"
                    : "bg-red-600 text-white"
                }`}
              >
                {item.status}
              </span>
              <span className="text-xs text-gray-600">{item.time}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function StatCircle({ label, value, color }: { label: string; value: number; color: string }) {
  return (
    <div className="flex flex-col items-center">
      <div
        className={`relative w-24 h-24 rounded-full border-8 border-${color}-500 flex items-center justify-center text-lg font-bold text-${color}-700`}
      >
        {value}%
      </div>
      <span className={`mt-2 text-${color}-700 font-semibold`}>{label}</span>
    </div>
  );
}
