import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

const emails = [
  {
    name: "Shannon Ledford",
    emails: ["shannon@laveaocl.com", "spikexarger@gmail.com"],
    status: "inbox",
    time: "25 mins ago",
  },
  {
    name: "BRIX ROBERTS",
    emails: ["ErikRabra@gmail.com", "ErikRoberts@examplex.com"],
    status: "spam",
    time: "53 mins ago",
  },
  {
    name: "Rodney Jones",
    emails: ["RoaneyJD@gmail.com", "TerryJBabertse@examples.com"],
    status: "inbox",
    time: "75 mins ago",
  },
  {
    name: "Amanda Flitsch",
    emails: ["Lauren.P-e@gmail.com", "Shannon@eomples.com"],
    status: "spam",
    time: "27 mins ago",
  },
  {
    name: "BRIX ROBERTS",
    emails: ["ErikRabra@gmail.com", "ErikRoberts@examplex.com"],
    status: "spam",
    time: "53 mins ago",
  },
  {
    name: "Rodney Jones",
    emails: ["RoaneyJD@gmail.com", "TerryJBabertse@examples.com"],
    status: "inbox",
    time: "75 mins ago",
  },
  {
    name: "Amanda Flitsch",
    emails: ["Lauren.P-e@gmail.com", "Shannon@eomples.com"],
    status: "spam",
    time: "27 mins ago",
  },
  {
    name: "Kim Woodruff",
    emails: ["ErikWoodult@gail.com"],
    status: "spam",
    time: "35 mins ago",
  },
  {
    name: "CYNDI B RICE",
    emails: ["Shannonngl@gmail.com"],
    status: "spam",
    time: "25 mins ago",
  },
];

const statusColors: Record<string, string> = {
  inbox: "bg-green-100 text-green-800",
  spam: "bg-red-100 text-red-800",
};

const DashboardPage = () => {
    const [search, setSearch] = useState("");
    const [inbox, setInbox] = useState("0");
    const [spam, setSpam] = useState("0");
    const [nofind, setNofind] = useState("");
    const [message, setMessage] = useState<string[]>([]);
    const containerRef = useRef<HTMLDivElement | null>(null);
    const onSearchClick = async (e: React.MouseEvent<HTMLButtonElement>) => {
        try {
            const res = await axios.post("http://localhost:5000/api/check", {
                search
            });

            if (res.data.message) {
                setMessage((prev) => [...prev, res.data.message]);
                setInbox(res.data.p_inbox);
                setSpam(res.data.p_spam);
                setNofind(res.data.p_nofind);
            } else {
                // Handle when no token is returned
                alert("Invalid credentials or search result not found.");
            }

        } catch (err: unknown) {
            if (axios.isAxiosError(err)) {
                console.error("Axios error:", err.response?.data || err.message);
                alert("A server error occurred. Please try again.");
            } else {
                console.error("Unexpected error:", err);
                alert("An unknown error occurred.");
            }
        }
    };

    useEffect(() => {
        setSearch("gistangjie@outlook.com");
        }, []);

    const scroll = (direction: "left" | "right") => {
        if (containerRef.current) {
        const scrollAmount = 300;
        containerRef.current.scrollBy({
            left: direction === "left" ? -scrollAmount : scrollAmount,
            behavior: "smooth",
        });
        }
    };

    return (
    <div className="min-h-screen bg-gray-50 p-4">
        {/* Header */}
        <div className="bg-blue-900 text-white px-6 py-4 flex justify-between items-center rounded-md">
        <h1 className="text-xl font-semibold">Email Inbox Checker</h1>
        <div className="relative flex-row w-1/3">
            <input
            type="text"
            placeholder="Enter keyword or email and press enter"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="w-full pl-4 pr-10 py-2 rounded-md text-black"
            />
            <span className="absolute right-2 top-2.5 text-green-600">
            🔍
            </span>
        </div>
        <button
            type="button"            
            onClick={onSearchClick}
            className="bg-blue-600 text-white w-40 py-2 rounded-md hover:bg-blue-700 transition"
        > Check
        </button>
        </div>

        {/* Stats */}
        <div className="flex justify-center gap-10 py-8">
            <StatCircle label="Inbox" value={75} color="green" />
            <StatCircle label="Spam" value={25} color="red" />
        </div>

        <div className="text-white px-6 py-4 border-0 rounded relative mb-4 bg-lightBlue-500">
            <span className="text-xl inline-block mr-5 align-middle">
                <i className="fas fa-bell"></i>
            </span>
            <span className="inline-block align-middle mr-8">
                <b className="capitalize">lightBlue!</b> This is a lightBlue alert - check it out!
            </span>
            <button className="absolute bg-transparent text-2xl font-semibold leading-none right-0 top-0 mt-4 mr-6 outline-none focus:outline-none">
                <span>×</span>
            </button>
        </div>

        <div>  
            <div className="w-20 p-4 rounded-lg m-4">
                <div className="flex items-center">
                    <div className="font-icon-list col-lg-2 col-md-3 col-sm-4 col-6">
                    <img
                        src="/gmail.png"
                        alt="icon"
                        className="w-[60px] mt-3 mx-auto"
                    />
                    <p className="text-sm mt-2">imaldkfjlaskdfjlas</p>
                    <p className="text-sm">2016</p>
                    </div>
                </div>
            </div>            
            <div
  ref={containerRef}
  className="overflow-x-auto whitespace-nowrap scroll-smooth scrollbar-hide"
>
  <div className="flex gap-4 px-2 min-w-max">
    {emails.map((item, idx) => (
      <div
        key={idx}
        className={`min-w-[240px] snap-start p-4 rounded-lg shadow ${
          item.status === "inbox" ? "bg-green-50" : "bg-red-50"
        }`}
      >
        <h3 className="font-bold text-gray-800 mb-1">{item.name}</h3>
        <div className="text-sm text-gray-700 space-y-1 mb-2">
          {item.emails.map((email, i) => (
            <p key={i}>{email}</p>
          ))}
        </div>
        <div className="flex justify-between items-center text-sm mt-3">
          <span
            className={`px-3 py-1 rounded-lg text-xs font-semibold ${
              item.status === "inbox"
                ? "bg-green-500 text-white"
                : "bg-red-500 text-white"
            }`}
          >
            {item.status}
          </span>
          <span className="text-gray-500 text-xs">{item.time}</span>
        </div>
      </div>
    ))}
  </div>
</div>



        </div>
        {/* Email Results */}
        
    </div>
    );
};

type Color = "red" | "green" | "blue";

interface StatCircleProps {
  label: string;
  value: number;
  color: Color;
}

const colorMap = {
  red: {
    border: "border-red-500",
    text: "text-red-700",
  },
  green: {
    border: "border-green-500",
    text: "text-green-700",
  },
  blue: {
    border: "border-blue-500",
    text: "text-blue-700",
  },
};

function StatCircle({ label, value, color }: { label: string; value: number; color: string }) {
  return (
    <div className="flex flex-col items-center">
      <div
        className={`relative w-24 h-24 rounded-full border-8 border-${color}-500 flex items-center justify-center text-lg font-bold text-${color}-700`}
      >
        {value}%<br/>{label}
      </div>

    </div>
  );
};

export default DashboardPage;
