import React from "react";

const emails = [
  {
    name: "Shannon Ledford",
    emails: ["shannon@laveaocl.com", "spikexarger@gmail.com"],
    status: "inbox",
    time: "25 mins ago",
  },
  {
    name: "BRIX ROBERTS",
    emails: ["ErikRabra@gmail.com", "ErikRoberts@examplex.com"],
    status: "spam",
    time: "53 mins ago",
  },
  {
    name: "Rodney Jones",
    emails: ["RoaneyJD@gmail.com", "TerryJBabertse@examples.com"],
    status: "inbox",
    time: "75 mins ago",
  },
  {
    name: "Amanda Flitsch",
    emails: ["Lauren.P-e@gmail.com", "Shannon@eomples.com"],
    status: "spam",
    time: "27 mins ago",
  },
  {
    name: "Kim Woodruff",
    emails: ["ErikWoodult@gail.com"],
    status: "spam",
    time: "35 mins ago",
  },
  {
    name: "CYNDI B RICE",
    emails: ["Shannonngl@gmail.com"],
    status: "spam",
    time: "25 mins ago",
  },
];

const statusColors: Record<string, string> = {
  inbox: "bg-green-100 text-green-800",
  spam: "bg-red-100 text-red-800",
};

const DashboardPage = () => {
  return (
    <div className="min-h-screen bg-gray-50 p-4">
      {/* Header */}
      <div className="bg-blue-900 text-white px-6 py-4 flex justify-between items-center rounded-md">
        <h1 className="text-xl font-semibold">Email Inbox Checker</h1>
        <div className="relative w-1/3">
          <input
            type="text"
            placeholder="Enter keyword or email and press enter"
            className="w-full pl-4 pr-10 py-2 rounded-md text-black"
          />
          <span className="absolute right-2 top-2.5 text-green-600">
            üîç
          </span>
        </div>
      </div>

      {/* Stats */}
      <div className="flex justify-center gap-8 mt-10 mb-6">
        <div className="text-center">
          <div className="w-24 h-24 rounded-full border-8 border-green-500 border-t-white mx-auto"></div>
          <p className="mt-2 font-medium text-green-600">Inbox<br />75%</p>
        </div>
        <div className="text-center">
          <div className="w-24 h-24 rounded-full border-8 border-red-500 border-t-white mx-auto"></div>
          <p className="mt-2 font-medium text-red-600">Spam<br />25%</p>
        </div>
      </div>

      {/* Email Results */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {emails.map((item, idx) => (
          <div
            key={idx}
            className={`p-4 rounded-lg shadow ${
              item.status === "inbox" ? "bg-green-50" : "bg-red-50"
            }`}
          >
            <h3 className="font-bold text-gray-800 mb-1">{item.name}</h3>
            <div className="text-sm text-gray-700 space-y-1 mb-2">
              {item.emails.map((email, i) => (
                <p key={i}>{email}</p>
              ))}
            </div>
            <div className="flex justify-between items-center text-sm mt-3">
              <span
                className={`px-3 py-1 rounded-full text-xs font-semibold ${
                  item.status === "inbox"
                    ? "bg-green-600 text-white"
                    : "bg-red-600 text-white"
                }`}
              >
                {item.status}
              </span>
              <span className="text-gray-500 text-xs">{item.time}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DashboardPage;
