import React, { useState, useEffect, useRef } from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { Search } from "lucide-react";
import axios from "axios";

const emails = [
  {
    name: "Shannon Ledford",
    emails: ["shannon@laveaocl.com", "spikexarger@gmail.com"],
    status: "inbox",
    time: "25 mins ago",
  },
  {
    name: "BRIX ROBERTS",
    emails: ["ErikRabra@gmail.com", "ErikRoberts@examplex.com"],
    status: "spam",
    time: "53 mins ago",
  },
  {
    name: "Rodney Jones",
    emails: ["RoaneyJD@gmail.com", "TerryJBabertse@examples.com"],
    status: "inbox",
    time: "75 mins ago",
  },
  {
    name: "Amanda Flitsch",
    emails: ["Lauren.P-e@gmail.com", "Shannon@eomples.com"],
    status: "spam",
    time: "27 mins ago",
  },
  {
    name: "BRIX ROBERTS",
    emails: ["ErikRabra@gmail.com", "ErikRoberts@examplex.com"],
    status: "spam",
    time: "53 mins ago",
  },
  {
    name: "Rodney Jones",
    emails: ["RoaneyJD@gmail.com", "TerryJBabertse@examples.com"],
    status: "inbox",
    time: "75 mins ago",
  },
  {
    name: "Amanda Flitsch",
    emails: ["Lauren.P-e@gmail.com", "Shannon@eomples.com"],
    status: "spam",
    time: "27 mins ago",
  },
  {
    name: "Kim Woodruff",
    emails: ["ErikWoodult@gail.com"],
    status: "spam",
    time: "35 mins ago",
  },
  {
    name: "CYNDI B RICE",
    emails: ["Shannonngl@gmail.com"],
    status: "spam",
    time: "25 mins ago",
  },
];

const emailSections = [
  {
    title: "Leads from Website",
    emails: [
      { name: "Alice", emails: ["alice@example.com"], status: "inbox", time: "1 min ago" },
      { name: "Bob", emails: ["bob@spam.com"], status: "spam", time: "3 mins ago" },
    ],
  },
  {
    title: "Newsletter Subscriptions",
    emails: [
      { name: "Charlie", emails: ["charlie@news.com"], status: "inbox", time: "10 mins ago" },
      { name: "Dana", emails: ["dana@junk.com"], status: "spam", time: "15 mins ago" },
    ],
  },
  {
    title: "Support Tickets",
    emails: [
      { name: "Eve", emails: ["eve@support.com"], status: "inbox", time: "20 mins ago" },
    ],
  },
  // ... up to 10 different sections
];


const statusColors: Record<string, string> = {
  inbox: "bg-green-100 text-green-800",
  spam: "bg-red-100 text-red-800",
};

const DashboardPage = () => {
  const [search, setSearch] = useState("");
  const [inbox, setInbox] = useState("0");
  const [spam, setSpam] = useState("0");
  const [nofind, setNofind] = useState("");
  const [message, setMessage] = useState<string[]>([]);
  const containerRefs = useRef<Array<HTMLDivElement | null>>([]);

  const percentage = 75;

  const radius = 50;
  const stroke = 10;
  const normalizedRadius = radius - stroke / 2;
  const circumference = normalizedRadius * 2 * Math.PI;
  const strokeDashoffset = circumference - (percentage / 100) * circumference;

  const onSearchClick = async (e: React.MouseEvent<HTMLButtonElement>) => {
    try {
      const res = await axios.post("http://localhost:5000/api/check", {
        search
      });

      if (res.data.message) {
        setMessage((prev) => [...prev, res.data.message]);
        setInbox(res.data.p_inbox);
        setSpam(res.data.p_spam);
        setNofind(res.data.p_nofind);
      } else {
        // Handle when no token is returned
        alert("Invalid credentials or search result not found.");
      }

    } catch (err: unknown) {
      if (axios.isAxiosError(err)) {
        console.error("Axios error:", err.response?.data || err.message);
        alert("A server error occurred. Please try again.");
      } else {
        console.error("Unexpected error:", err);
        alert("An unknown error occurred.");
      }
    }
  };

  useEffect(() => {
    setSearch("gistangjie@outlook.com");
  }, []);

  const scroll = (index: number, direction: "left" | "right") => {
    const container = containerRefs.current[index];
    if (container) {
      const scrollAmount = 300;
      container.scrollBy({
        left: direction === "left" ? -scrollAmount : scrollAmount,
        behavior: "smooth",
      });
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      {/* Header */}
      <div className="flex justify-end w-full bg-[#174866] p-4">
        <div className="flex w-[400px] bg-white rounded shadow overflow-hidden">
          <input
            type="text"
            placeholder="Enter keyword or email and press enter"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && onSearchClick}
            className="flex-1 px-4 py-2 text-sm text-gray-700 focus:outline-none"
          />
          <button
            onClick={onSearchClick}
            className="bg-green-500 hover:bg-green-600 px-4 flex items-center justify-center"
          >
            <Search className="text-white w-4 h-4" />
          </button>
        </div>
      </div>

      {/* Stats */}
      <div className="flex justify-center gap-10 py-8">
        <StatCircle label="Inbox" value={75} color="green" />
        <StatCircle label="Spam" value={25} color="red" />
      </div>

      {emailSections.map((section, blockIndex) => (
        <div key={blockIndex} className="message-item flex gap-4 items-start ">
          {/* Left static card */}
          <div className=" w-[250px] h-[130px] p-2 px-[10px] pb-[10px] border rounded-lg shadow flex flex-col items-center">
            <img src="/gmail.png" alt="icon" className="w-[60px] mt-3" />
            <p className="text-base mt-2 text-center ">radugistang1@gmail.com</p>
            <p className="text-sm text-center text-green-700"><b>valid</b></p>
          </div>

          {/* Scrollable area with arrows */}
          <div className="relative w-[90%] flex items-center">
            {/* Left Button */}
            <button
              onClick={() => scroll(blockIndex, "left")}
              className="absolute left-2 top-1/2 transform -translate-y-1/2 z-10 bg-white shadow p-2 rounded-full hover:bg-gray-100"
            >
              <ChevronLeft className="w-5 h-5" />
            </button>

            {/* Scrollable Area */}
            <div
              ref={(el) => {
                containerRefs.current[blockIndex] = el;
              }}
              className="overflow-x-auto whitespace-nowrap scroll-smooth scrollbar-hide"
            >
              <div className="flex gap-4 min-w-max snap-x">
                {section.emails.map((item, idx) => (
                  <div
                    key={`${blockIndex}-${idx}`}
                    className={`w-[270px] h-[130px]  border snap-start p-4 rounded-lg shadow ${item.status === "inbox" ? "bg-green-50" : "bg-red-50"
                      }`}
                  >
                    <h3 className="font-bold text-gray-800 mb-1">{item.name}</h3>
                    <div className="text-sm text-gray-700 space-y-1 mb-2">
                      {item.emails.map((email, i) => (
                        <p key={i}>{email}</p>
                      ))}
                    </div>
                    <div className="flex justify-between items-center text-sm mt-[25px]">
                      <span
                        className={`px-3 py-1 rounded-lg text-xs font-semibold  ${item.status === "inbox"
                          ? "bg-green-500 text-white"
                          : "bg-red-500 text-white"
                          }`}
                      >
                        {item.status}
                      </span>
                      <span className="text-gray-500 text-xs">{item.time}</span>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Right Button */}
            <button
              onClick={() => scroll(blockIndex, "right")}
              className="absolute right-2 top-1/2 transform -translate-y-1/2 z-10 bg-white shadow p-2 rounded-full hover:bg-gray-100"
            >
              <ChevronRight className="w-5 h-5" />
            </button>
          </div>
        </div>
      ))}


      {/* Email Results */}
    </div>
  );
};

type Color = "red" | "green" | "blue";

interface StatCircleProps {
  label: string;
  value: number;
  color: Color;
}

const colorMap = {
  red: {
    border: "border-red-500",
    text: "text-red-700",
  },
  green: {
    border: "border-green-500",
    text: "text-green-700",
  },
  blue: {
    border: "border-blue-500",
    text: "text-blue-700",
  },
};

function StatCircle({ label, value, color }: StatCircleProps) {
  const radius = 45;
  const stroke = 10;
  const normalizedRadius = radius - stroke / 2;
  const circumference = normalizedRadius * 2 * Math.PI;
  const strokeDashoffset =
    circumference - (value / 100) * circumference;

  return (
    <div className="relative w-24 h-24 flex items-center justify-center">
      <svg
        height="100%"
        width="100%"
        className="rotate-[-90deg]"
      >
        {/* Background circle */}
        <circle
          stroke="#e5e7eb" // gray-200
          fill="transparent"
          strokeWidth={stroke}
          r={normalizedRadius}
          cx="48"
          cy="48"
        />
        {/* Progress circle */}
        <circle
          stroke={color} // Fallback if dynamic color fails
          className={`stroke-${color}-500`} // Tailwind dynamic stroke
          fill="transparent"
          strokeWidth={stroke}
          strokeDasharray={circumference}
          strokeDashoffset={strokeDashoffset}
          strokeLinecap="round"
          r={normalizedRadius}
          cx="48"
          cy="48"
        />
      </svg>

      {/* Center Text */}
      <div className="absolute text-center text-sm font-semibold text-black">
        <p>{label}</p>
        <p>{value}%</p>
      </div>
    </div>
  );
}

export default DashboardPage;
