type Color = "red" | "green" | "blue";

interface StatCircleProps {
  label: string;
  value: number;
  color: Color;
}

const colorMap = {
  red: {
    border: "border-red-500",
    text: "text-red-700",
  },
  green: {
    border: "border-green-500",
    text: "text-green-700",
  },
  blue: {
    border: "border-blue-500",
    text: "text-blue-700",
  },
};

function StatCircle({ label, value, color }: { label: string; value: number; color: string }) {
  return (
    <div className="flex flex-col items-center">
      <div
        className={`relative w-24 h-24 rounded-full border-8 border-${color}-500 flex items-center justify-center text-lg font-bold text-${color}-700`}
      >
        {value}%<br />{label}
      </div>

    </div>
  );
};

