import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";

export default function FlexRowCarousel() {
  const cards = Array.from({ length: 10 }, (_, i) => ({
    title: `Card ${i + 1}`,
    content: `This is card ${i + 1}`,
  }));

  return (
    <div className="flex items-center gap-6 p-6">
      {/* Some other content on the left */}
      <div className="w-1/4 bg-gray-200 p-4 rounded-xl">
        <h2 className="text-xl font-bold">Left Content</h2>
        <p className="text-gray-600">This stays beside the carousel.</p>
      </div>

      {/* Carousel on the right */}
      <div className="flex-1">
        <Swiper
          modules={[Navigation]}
          spaceBetween={20}
          slidesPerView={4}
          navigation
          loop={false}
          className="w-full"
        >
          {cards.map((card, index) => (
            <SwiperSlide key={index}>
              <div className="bg-white rounded-2xl shadow-lg p-6 text-center">
                <h2 className="text-lg font-bold">{card.title}</h2>
                <p className="text-gray-600">{card.content}</p>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </div>
  );
}
